from functools import lru_cache

def brute_force(mochi):
    n = len(mochi)
    N = 1 << n
    dp = [False] * N
    dp[0] = True
    for mask in range(1, N):
        cnt = mask.bit_count()
        if cnt % 2 == 1:
            continue
        # find smallest index i in mask
        i = 0
        while i < n and not ((mask >> i) & 1):
            i += 1
        if i >= n:
            continue
        sub = mask ^ (1 << i)
        j = i + 1
        while j < n:
            if (mask >> j) & 1:
                if mochi[i] * 2 <= mochi[j]:
                    if dp[sub ^ (1 << j)]:
                        dp[mask] = True
                        break
            j += 1
    ans = 0
    for mask in range(N):
        if dp[mask]:
            k = mask.bit_count() // 2
            if k > ans:
                ans = k
    return ans

